import React from 'react';
import { Routes, Route, BrowserRouter } from 'react-router-dom';
import routes from './routes';
import { useAuth } from './AuthContext';
import Login from '../pages/Login';

const PrivateRoute = ({ element, ...rest }) => {
    const { authenticated } = useAuth();

    return (
        <React.Fragment>
            {
                authenticated ? <Route {...rest} element={element} /> :
                    <Route path="/login" element={element} />
            }
        </React.Fragment>
    );
};

const AppRouter = () => {
    const { authenticated } = useAuth();

    return (
        <BrowserRouter>
            <Routes>
                {routes.map(({ path, component, exact, isPrivate }, index) => (
                    isPrivate ? (
                        <Route
                            key={index}
                            path={authenticated ? path : "/login"}
                            element={authenticated ? <component/> : <Login/>}  // Corrected this line
                            exact={exact}
                        />
                    ) : (
                        <Route
                            key={index}
                            path={path}
                            element={<component />}  // Corrected this line
                            exact={exact}
                        />
                    )
                ))}
            </Routes>
        </BrowserRouter>
    );
};

export default AppRouter;
