import React from 'react';
import { Routes, Route, BrowserRouter } from 'react-router-dom';
import routes from './routes';
import { useAuth } from './AuthContext';

const PrivateRoute = ({ element: Component, ...rest }) => {
    const { authenticated } = useAuth();

    return (
        <React.Fragment>
            {
                authenticated ? <Route {...rest} element={<Component />} /> :
                    <Route path="/login" />
            }
        </React.Fragment>
    );
};

const AppRouter = () => {
    return (
        <BrowserRouter>
            <Routes>
                {routes.map(({ path, component, exact, isPrivate }, index) => (
                    isPrivate ? (
                        <PrivateRoute
                            key={index}
                            path={path}
                            element={component}
                            exact={exact}
                        />
                    ) : (
                        <Route
                            key={index}
                            path={path}
                            element={<component/>}
                            exact={exact}
                        />
                    )
                ))}

            </Routes>
        </BrowserRouter>
    );
};

export default AppRouter;
