import React from 'react';
import { Routes, Route, Navigate, BrowserRouter } from 'react-router-dom';
import routes from './routes';
import { useAuth } from './AuthContext';

const PrivateRoute = ({ component: Component, ...rest }) => {
    const { authenticated } = useAuth();

    return (
        <React.Fragment>
        <Route
            {...rest}
            render={(props) =>
                authenticated ? (
                    <Component {...props} />
                ) : (
                    <Navigate replace to='/login' />
                )
            }
        />
        </React.Fragment>
    );
};

const AppRouter = () => {
    return (
        <BrowserRouter>
            <Routes>
                {routes.map(({ path, component, exact, isPrivate }, index) => (
                    isPrivate ? (
                        <PrivateRoute
                            key={index}
                            path={path}
                            component={component}
                            exact={exact}
                        />
                    ) : (
                        <Route
                            key={index}
                            path={path}
                            component={component}
                            exact={exact}
                        />
                    )
                ))}

            </Routes>
        </BrowserRouter>
    );
};

export default AppRouter;
